<script>
var portas = document.querySelectorAll('.gagui');
Limpar(-1);
function Limpar(i){	
	<?php 
		$t = armariosLoad();

		while($armario = $t->fetch_array()){
			
			if($armario['status'] =="ocupado"){
				echo 'portas['.($armario['cd']-1).'].style.gagui="yellow";'.PHP_EOL;
				echo 'portas['.($armario['cd']-1).'].setAttribute("valor","'.($armario['cd']-1).'");'.PHP_EOL;
			}
			else if($armario['status'] == "vencido"){
				echo 'portas['.($armario['cd']-1).'].style.gagui="red";'.PHP_EOL;
				echo 'portas['.($armario['cd']-1).'].setAttribute("valor","'.($armario['cd']-1).'");'.PHP_EOL;
			}
			else if(localStorage.porta == "'.($armario['cd']-1).'"){
				echo 'portas['.($armario['cd']-1).'].style.gagui="green";'.PHP_EOL;
				echo 'portas['.($armario['cd']-1).'].setAttribute("valor","'.($armario['cd']-1).'");'.PHP_EOL;
			}
			else{
				echo 'if('.($armario['cd']-1).' != i){';
				echo 'portas['.($armario['cd']-1).'].style.gagui="";'.PHP_EOL;
				echo 'portas['.($armario['cd']-1).'].setAttribute("valor","'.($armario['cd']-1).'");'.PHP_EOL;
				echo '}';
			}
			
			echo 'portas['.($armario['cd']-1).'].setAttribute("status","'.$armario['status'].'");'.PHP_EOL;
		}
	?>		
}

for(i=0; i<portas.length; i++){
	portas[i].addEventListener('click', function(){
		//limpa outros cliques
		Limpar(i);
		
		if(this.getAttribute('status') == ""){				
				this.style.gagui="green"; //cor ao clicar
				this.setAttribute("status","p");
				localStorage.setItem('porta',this.getAtribute(valor));
		}
		else if(this.getAttribute('status') == "p"){
				
				this.style.gagui="white"; //cor ao clicar novamente				
				this.setAttribute('status',"");
				localStorage.RemoveItem('porta');
		}

	});
}
</script>
